INSERT INTO `default_page_types` VALUES ('3', 'accueil_msante', 'Accueil MSant√©', 'Page d‚Äôaccueil MSant√©', '2', '', null, '', '{{ dev:empty_cache }}\r\n<!-- accueil msante page type -->\r\n{{if !custom:subdomain_value and page:is_home }} \r\n<div id=\"categories\"> \r\n    <div class=\"text-center h3 titre-rdv\"> \r\n        Prenez rendez vous avec votre professionnel de sant&eacute; \r\n    </div> \r\n</div>\r\n\r\n\r\n<div class=\"h3 text-center titre-auto\">   \r\n</div>\r\n{{# doctor:search_box #}} \r\n<div id=\"doctor-search\" class=\"\">\r\n                <!-- doctor search -->\r\n            <!--\r\n    <form method=\"get\" action=\"{{url:site}}doctor\">\r\n        <div class=\"form-group searchbox-query\">\r\n            <div class=\"input-group\">\r\n                <span class=\"input-group-addon\">\r\n                    <a class=\"\" onclick=\"cleanDocSearch()\">\r\n                        <i class=\"glyphicon glyphicon-remove-circle\"></i>\r\n                    </a>\r\n                </span>\r\n                <input type=\"text\" name=\"c\" value=\"\" placeholder=\"Domaine ou sp√©cialit√© m√©dicale\" class=\"form-control ui-autocomplete-input\" autocomplete=\"off\">\r\n            </div> \r\n        </div> \r\n        <div class=\"form-group searchbox-locate\"> \r\n                <div class=\"input-group center-block\">\r\n                    <a class=\"btn btn-success disabled\" onclick=\"geoGetTown()\">\r\n                        <i class=\"glyphicon glyphicon-map-marker\"></i> Autour de vous\r\n                    </a>\r\n                </div> \r\n            </div>\r\n        <div class=\"form-group searchbox-area\"> \r\n            <div class=\"input-group\">\r\n                <span class=\"input-group-addon\">\r\n                    <a class=\"\" onclick=\"cleanAreaSearch()\">\r\n                        <i class=\"glyphicon glyphicon-remove-circle\"></i>\r\n                    </a>\r\n                </span>\r\n                <input type=\"text\" name=\"s\" value=\"\" placeholder=\"Ville, quartier ou CP\" class=\"form-control\">                  \r\n            </div>\r\n        </div> \r\n        <div class=\"form-group searchbox-btn\"> \r\n                <button name=\"submitBtn\" type=\"submit\" class=\"btn btn-primary\">\r\n                        <i class=\"glyphicon glyphicon-search\"></i> Rechercher\r\n                </button> \r\n        </div>\r\n    </form>\r\n            -->\r\n</div>\r\n\r\n{{endif}}\r\n\r\n{{# dessous gabarit des pages normales, sauf titre H2  #}}\r\n\r\n{{ pre_html }}\r\n{{ body }}\r\n{{ post_html }}', '.ico-grp { \r\n    max-width: 314px;\r\n}\r\n.specialite {\r\ndisplay: inline-block;\r\n    margin: 0%;\r\n    width: 100px;\r\n    text-align: center;\r\n    /* max-width: 33%; */\r\n    min-width: 85px;\r\n    height: 100px;\r\n    padding: 3%;\r\n    /* background: red; */\r\n}\r\ndiv.specialite:hover {\r\n    font-weight : bold;\r\n}\r\n#doctor-search {\r\n    margin: auto;\r\n    text-align: center;\r\n    max-width: 400px;\r\n} \r\n.set {\r\n    background-color: #dff0d8;\r\n    font-weight: bold;\r\n    color: #3c763d;\r\n}\r\ndiv.specialite p {\r\n    text-overflow: ellipsis;\r\n    text-align: center; \r\n    width: 100%;\r\n    height: 38px;\r\n    overflow: hidden;\r\n    font-size: small;\r\n}', '/* public vars */\r\nvar doctors = []; \r\nvar mApi = { \r\n    mapsUrl: \"https://maps.googleapis.com/maps/api/js?key=\",\r\n    key:\"AIzaSyDkUU_ti16t_PBfJ8o2-p_AciVkD8Q_2uo\",\r\n    geocodeUrl:\"https://maps.googleapis.com/maps/api/geocode/json?sensor=false&address=\",\r\n//    centLat:48.62,\r\n//    centLon:2.44, \r\n    zoom:10\r\n}; \r\n/* doctor module functions */\r\n/** builds locations map of doctors\r\n *  gets data from HTML5 data attributes in doctor\'s list\r\n * @returns {undefined}\r\n */\r\nfunction doctorsMap() \r\n{\r\n//    console.log(\'doctorsMap\');\r\n//    console.log(doctors);\r\n    if(typeof doctors === \'undefined\') return;\r\n  